package org.kymjs.kjframe.demo.widget;import org.kymjs.kjframe.KJActivity;import org.kymjs.kjframe.demo.R;import org.kymjs.kjframe.ui.BindView;import org.kymjs.kjframe.ui.ViewInject;import android.view.View;import android.widget.TextView;public class KJSlidingMenuDemo extends KJActivity {    @BindView(id = R.id.textview, click = true)    private TextView mText;    @Override    public void setRootView() {        setContentView(R.layout.aty_main);    }    @Override    public void widgetClick(View v) {        super.widgetClick(v);        switch (v.getId()) {        case R.id.textview:            ViewInject.toast("hello");            break;        default:            break;        }    }}